6.query:
fetch dt.entity.kubernetes_cluster 
| fields id, entity.name
| filter entity.name == $Cluster
| lookup [
  timeseries {
      valuesOp1 = sum(dt.kubernetes.container.cpu_usage),
      valuesOp2 = sum(dt.kubernetes.node.cpu_allocatable)
    }, by:{dt.entity.kubernetes_cluster}, from: -2m
    | fieldsAdd cpu_usage_percent = valuesOp1[] / valuesOp2[] * 100
    | fieldsAdd cpu_usage_percent = arrayFirst(cpu_usage_percent)
    | limit 20000
], sourceField:id, lookupField:dt.entity.kubernetes_cluster, fields:{cpu_usage_percent}

