import { problemsClient } from "@dynatrace-sdk/client-classic-environment-v2";

let probs = [];
const categoryOrder = { '🔴': 1, '🟨': 2, ' ': 3 };

async function getProblems() {
  let probS = "";
  if($OS == "WINDOWS") probS += "entityTags(\"OS:Windows\"),";
  else if ($OS == "LINUX") probS += "entityTags(\"OS:Linux\"),";
  if($ManagementZone != "ALL") probS += "managementZones(" + $ManagementZone.toString() + "),";
  probS += "status(\"open\")"; 
  const pQuery = await problemsClient.getProblems({
    from: Date.parse($dt_timeframe_from).toString(),
    to: Date.parse($dt_timeframe_to).toString(),    
    problemSelector: probS,
    pageSize: 500
  });
  probs = pQuery.problems;
  if(pQuery.hasOwnProperty("nextPageKey") && pQuery.nextPageKey != null) return await next(pQuery.nextPageKey);
  else return 1;
}

export default async function main() {
  const v = await getProblems();
  const d = (new Date()).getTime();
  const processedResults: MetricResult[] = probs.map(result => {
    let ARC = "null";
    if(result.hasOwnProperty("impactedEntities") && result.impactedEntities.length > 0) ARC = result.impactedEntities[0].name;
    if(result.hasOwnProperty("rootCauseEntity") && String(result.rootCauseEntity) != "undefined") ARC = result.rootCauseEntity.name;
    let lv = " ";
    if((result.title).includes("Critical")) lv = "🔴";
    else if((result.title).includes("Warning")) lv = "🟨";
    return {
      "Lv": lv,
      "Problem ID": result.displayId,
      "Title": result.title,
      "Assigned Root Cause": ARC, 
      "StartTime": new Date(result.startTime),
      "Length": (d - result.startTime) / (60000)
    };
  });
  processedResults.sort((a, b) => {
    let categoryComparison = categoryOrder[a.Lv] - categoryOrder[b.Lv];

    return categoryComparison !== 0 ? categoryComparison : b["Length"] - a["Length"];
  });

  const arr = []
  for (let i=0;i<processedResults.length;i++) {
    arr.push({...processedResults[i]})
  }
  return arr;
}

async function next(npk){
  const pfq = await problemsClient.getProblems({nextPageKey: npk});
  probs = probs.concat(pfq.problems);    
  if(pfq.hasOwnProperty("nextPageKey") && pfq.nextPageKey != null) return await next(pfq.nextPageKey);
  else return 1;
}

