import { settingsObjectsClient } from "@dynatrace-sdk/client-classic-environment-v2";
export default async function () {
  let mzs = [];
  let data = await settingsObjectsClient.getSettingsObjects({
    schemaIds: "builtin:management-zones",
    pageSize: 500
  });
  mzs = data.items;
  if(data.hasOwnProperty("nextPageKey") && data.nextPageKey != null){
    const q = await next(data.nextPageKey);
  }
  let arr = [];
  (mzs).map(result => {
    arr.push(result.value.name);
  });
  console.log(mzs.length);
  return ["Select a Management Zone"].concat(arr.sort());
  async function next(npk){
    const more = await settingsObjectsClient.getSettingsObjects({nextPageKey: npk});
    console.log(more);
    mzs = mzs.concat(more.items);
    if(more.hasOwnProperty("nextPageKey") && more.nextPageKey != null) return await next(more.nextPageKey);
    else return 1;
  }
}

