24.query:
timeseries { latest = avg(vmware.datastore.disk.provisioned.latest), capacity = avg(vmware.datastore.capacity) }, by: { device.address, dt.metrics.source, datastore.id, `dt.entity.vmware:datastore`, `dt.entity.vmware:vcenter`, datastore.name }
| fieldsAdd percent_provisioned = ((latest[] * 1024) / capacity[] ) * 100
| summarize { percent_provisioned = avg(percent_provisioned[] ) }, by: { `dt.entity.vmware:datastore`, timeframe, interval }
| fieldsAdd entityName(`dt.entity.vmware:datastore`)
| sort arrayAvg(percent_provisioned) desc
| limit 20

25.query:
timeseries { freeSpace = avg(vmware.datastore.freeSpace), capacity = avg(vmware.datastore.capacity) }, by: { device.address, dt.metrics.source, datastore.id, `dt.entity.vmware:datastore`, `dt.entity.vmware:vcenter`, datastore.name }
| fieldsAdd used = 100 - (100 * (freeSpace[] / capacity[] ))
| summarize { used = avg(used[] ) }, by: { `dt.entity.vmware:datastore`, timeframe, interval }
| fieldsAdd entityName(`dt.entity.vmware:datastore`)
| sort arrayAvg(used) desc
| limit 20

26.query:
timeseries { used_hz = avg(vmware.host.cpu.used_hz), total_hz = avg(vmware.host.cpu.total_hz) }, by: { `dt.entity.vmware:host`, cluster.name, device.address, `dt.entity.vmware:cluster`, `dt.entity.vmware:datacenter`, dt.metrics.source, datacenter.name, host.name, host.id, cluster.id, datacenter.id, `dt.entity.vmware:vcenter` }
| fieldsAdd capacity_used = (used_hz[] / total_hz[] ) * 100
| summarize { capacity_used = avg(capacity_used[] ) }, by: { `dt.entity.vmware:host`, timeframe, interval }
| fieldsAdd entityName(`dt.entity.vmware:host`)
| sort arrayAvg(capacity_used) desc
| limit 20
