input.value:
fetch logs
| filter aws.log_group == "flow-logs-us-west-1-cw" or aws.log_group =="flow-logs-us-west-1-tgw-cw" or aws.log_group =="flow-logs-eu-west-1-cw" or aws.log_group =="flow-logs-eu-west-1-tgw-cw"  or aws.log_group =="flow-logs-us-west-2-cw"  or aws.log_group =="flow-logs-us-west-2-tgw-cw"
| fieldsAdd  bytes,srcaddr,dstaddr,srcport,dstport,aws.log_group
| fields srcaddr,dstaddr,srcport,dstport,bytes
| filter isNotNull(srcaddr)
 // a few optional filters
//  | filter srcaddr > toIp("1.1.1.1") and srcaddr > toIp("1.1.1.1")
// | filter srcaddr == toIp("1.1.1.1") and srcaddr ==  toIp("1.1.1.1")
// | filter srcport == "80" or srcaddr ==  "443"
 | limit 10

timeseries t_fs=min( `com.dynatrace.extension.snmp-generic-cisco-device.envmon.temperature.status.value`), by: {`dt.entity.snmp:com_dynatrace_extension_snmp_generic_cisco_device`,temperature.status.descr}
| lookup [fetch  `dt.entity.snmp:com_dynatrace_extension_snmp_generic_cisco_device`| fields id, system_name],
    sourceField:`dt.entity.snmp:com_dynatrace_extension_snmp_generic_cisco_device`,
    lookupField:id,
    fields:{ system_name }    
| fieldsRename `DNS name` = system_name

timeseries t_cpu=avg(`com.dynatrace.extension.f5.bigip.sys.global.host.cpu.user1m`),by: {`dt.entity.f5:instance`}
| lookup [fetch `dt.entity.f5:instance` | fields id, entity.name, NodeName, lifetime[end],entity.type ],
    sourceField:`dt.entity.f5:instance`,
    lookupField:id,
    fields:{entity.name,NodeName,`lifetime[end]`, entity.type  }
| fieldsRemove entity.name,`dt.entity.f5:instance`
